import React,{ Component } from 'react';
import dchannel from './../img/dchannel.jpg';


class Login extends Component {
    constructor() {
        super();
        this.state= {
            Email:'',
            Password:''
        }
        this.Password = this.Password.bind(this);
        this.Email = this.Email.bind(this);
        this.Login = this.Login.bind(this);
    }

    Email(Event){
        this.setState({
            Email: Event.target.value
        });
        console.log("Hello Email");
    }

    Password(Event){
        this.setState({
            Email: Event.target.value
        });
        console.log("Hello Email");
    }
    Login(login) {
        // const login = {
        //     username: this.state.Email,
        //     password: this.state.Password
        // }
        // fetch('http://Api/Login', {
        //     method: 'post',
        //     headers: {
        //         'Accept': 'application/json',
        //         'Content-Type': 'application/json'
        //     }
    }
    render() {
        return(
            <div className="container">
                <div className="locgin-container">
                    <div className="dchannel-form">
                        <h3 className="text-center">Sign In</h3>
                        <div className="row">
                            <div className="col-md-6 float-right">
                                <div className="dchannel-logo">
                                    <img src={dchannel} alt="dchannel"/>
                                </div>
                            </div>
                            <div className="col-md-6 float-right">
                                <form>
                                    <div className="form-group">
                                        <input type="text" className="form-control" placeholder="Your Email" onChange={this.Email}/>
                                    </div>
                                    <div className="form-group">
                                        <input type="password" className="form-control" placeholder="Your Password *" onChange={this.Password}/>
                                    </div>
                                    <div className="form-group text-center">
                                        <button className="btn yellow" onClick={this.Login}>Login</button>
                                        {/*<button className="form-control btn yellow" onClick={this.login}>Login</button>*/}
                                    </div>
                                    {/*<div className="form-group">*/}
                                    {/*    <p className="text-center"><Link to="/register" className="ForgetPwd" value="register">REGISTER</Link> </p>*/}
                                    {/*</div>*/}
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default Login;
