import React,{ Component } from 'react';
import {NavLink} from "react-router-dom";

class Allproduct extends Component {
    constructor() {
        super();
        this.state= {
            allProduct: []
        }
        this.detail = this.detail.bind(this);
    }

    componentWillMount() {
        const url = "api";
        fetch(url).then(response => {
            return response.json();
        })
            .then(result => {
                this.setState({ allProduct: result });
                // console.log(this.state.allProduct)
            })
            .catch(error => console.log(error))
    }

    detail() {
        console.log("Hello Detail");
        this.props.history.push('/dashboard/product/product-detail');
    }

    render() {
        const products = this.state.allProduct;
        return (
            <div>
                <div className="pro-header">
                    <div className="p-2 mr-auto">
                        <div className="input-group md-form form-sm form-2 pl-0">
                            <input className="form-control my-0 py-1 red-border" type="text" placeholder="Search"
                                   aria-label="Search"/>
                        </div>
                    </div>
                    <div className="p-2">
                        <button className="btn btn-gray">
                            <NavLink to="/dashboard/product/productnew" activeClassName="tab-active">New</NavLink>
                        </button>
                    </div>
                </div>
                <div className="dchannel-content">
                    <div style={{ padding: "10px"}}>
                        <table style={{ width: "100%"}}>
                            <thead>
                            <tr>
                                <th>Stock Code</th>
                                <th>
                                    <select name="" id="">
                                        <option value="">All Brand</option>
                                        <option value="">Royal</option>
                                        <option value="">Bella</option>
                                    </select>
                                </th>
                                <th>Type</th>
                                <th>Price</th>
                                <th>Stock</th>
                            </tr>
                            </thead>
                            <tbody>
                            {
                                products.map((product) =>
                                    <tr key={product.id} onClick={() => this.detail(product.id) }>
                                        <td>{product.stockCode}</td>
                                        <td>{product.brand}</td>
                                        <td>{product.type}</td>
                                        <td>{product.price}</td>
                                        <td>{product.stock}</td>
                                    </tr>

                                )
                            }
                            <tr onClick={() => this.detail() }>
                                <td>3242</td>
                                <td>Royal</td>
                                <td>Cream</td>
                                <td>8000</td>
                                <td>5</td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }
}
export default Allproduct;
