import React, { Component } from 'react'
import {NavLink} from "react-router-dom";

class CustomerForm extends Component{
    constructor() {
        super();
        this.state = {
            PhoneNo: '',
            Email: '',
            ShopName: '',
            Address: '',
            PaymentTerm: ''
        }
        this.PhoneNo = this.PhoneNo.bind(this);
        this.Email = this.Email.bind(this);
        this.ShopName = this.ShopName.bind(this);
        this.Address = this.Address.bind(this);
        this.PaymentTerm = this.PaymentTerm.bind(this);
    }
    PhoneNo(Event){
        this.setState({
            PhoneNo: Event.target.value
        });
        console.log("Hello PhoneNo");
    }
    Email(Event){
        this.setState({
            Email: Event.target.value
        });
        console.log("Hello PhoneNo");
    }
    ShopName(Event){
        this.setState({
            ShopName: Event.target.value
        });
        console.log("Hello PhoneNo");
    }
    Address(Event){
        this.setState({
            Address: Event.target.value
        });
        console.log("Hello PhoneNo");
    }
    PaymentTerm(Event){
        this.setState({
            PaymentTerm: Event.target.value
        });
        console.log("Hello PhoneNo");
    }
    render() {
        return(
            <div>
                <div className="pro-header">
                    <div className="p-2 mr-auto">
                        <button className="btn btn-gray">
                            <NavLink to="/dashboard/customers">Back</NavLink>
                        </button>
                    </div>
                    <div className="p-2 ml-auto">
                        <button className="btn btn-gray">
                            <NavLink to="/dashboard/customers">Save</NavLink>
                        </button>
                    </div>
                </div>
                <div className="dchannel-content" id="deliver">
                    <div style={{ padding: "10px"}}>
                        <div className="row di-mg">
                            <div className="col-md-6">
                                <div className="row">
                                    <div className="col-md-3">
                                        <label>Phone No</label>
                                    </div>
                                    <div className="col-md-9">
                                        <input type="text" onChange={this.PhoneNo}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row di-mg">
                            <div className="col-md-6">
                                <div className="row">
                                    <div className="col-md-3">
                                        <label>Email</label>
                                    </div>
                                    <div className="col-md-9">
                                        <input type="text" onChange={this.Email}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row di-mg">
                            <div className="col-md-6">
                                <div className="row">
                                    <div className="col-md-3">
                                        <label>Shop Name</label>
                                    </div>
                                    <div className="col-md-9">
                                        <input type="text" onChange={this.ShopName}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row di-mg">
                            <div className="col-md-6">
                                <div className="row">
                                    <div className="col-md-3">
                                        <label>Address</label>
                                    </div>
                                    <div className="col-md-9">
                                        <textarea name="" id=""  cols={22
                                        }  onChange={this.Address}></textarea>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row di-mg">
                            <div className="col-md-6">
                                <div className="row">
                                    <div className="col-md-3">
                                        <label>Payment Term</label>
                                    </div>
                                    <div className="col-md-9">
                                        <select name="" id="" style={{ width: "180px"}}>
                                            <option value="">Cash</option>
                                            <option value="">Credit</option>
                                            <option value="">Consignment</option>
                                        </select>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        );
    }
}
export default CustomerForm;
