import React,{ Component } from 'react';
import dchannel from './../img/dchannel.jpg';
import { Link } from 'react-router-dom';
class Login extends Component {

    constructor() {
        super();
        this.state= {
            Email:'',
            Password:''
        }
        this.Password = this.Password.bind(this);
        this.Email = this.Email.bind(this);
        this.Login = this.Login.bind(this);
    }

    Email(Event){
        this.setState({
            Email: Event.target.value
        });
    }

    Password(Event){
        this.setState({
            Email: Event.target.value
        });
    }
    Login() {
        // console.log(this.state.Email);
        // console.log(this.state.Password);

        this.props.history.push("/dashboard/order");
        fetch('http://Api/Login', {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                Email: this.state.Email,
                Password: this.state.Password
            })
        }).then((Response) => Response.json())
            .then((result) => {
                console.log(result);
                if (result.Status == 'True')
                    this.props.history.push("/dashboard/order");
            })
    }

    render() {
        return(
            <div className="wrap">
                <div className="container">
                    <div className="login">
                        <div className="title-bar">
                            <p>Login</p>
                        </div>
                        <div className="form-content">
                            <p className="text-right"><a href=" ">Login with Email</a></p>
                            <form className="form">
                                <input type="text" name="" placeholder="Email" required=""/>
                                    <div className="phone">
                                        <select>
                                            <option>+95</option>
                                            <option>+01</option>
                                            <option>+067</option>
                                        </select>
                                        <input type="text" name="" placeholder="Phone No" required=""/>
                                    </div>
                                    <input type="password" name="" placeholder="Password" required=""/>
                                        <button className="btn-com"><a href="dashboard.html"
                                                                       className="cl-white">Login</a></button>
                            </form>
                            <p className="text-right">
                                <Link to="/register" className="cl-white" value="register">REGISTER</Link>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default Login;
